import { LOGIN, SIGNUP } from "../actions/types";

const INITAL_STATE = {};
export default (state = INITAL_STATE, action) => {
  switch (action.type) {
    case LOGIN:
      return { ...state, repos: action.payload, loading: true };
    case SIGNUP:
      return { ...state, addedToBookmark: action.payload };
    default:
      return state;
  }
};
