import api from "../../apis/api";

import {
  SEARCH_REPO,
  ADD_BOOKMARK,
  DELETE_BOOKMARK,
  FETCH_BOOKMARKS,
} from "./types";

export const searchRepo = (searchterm) => async (dispatch, getState) => {
  const response = await api.get(`/search/${searchterm}`);
  dispatch({ type: SEARCH_REPO, payload: response.data });
};

export const addBookmark = () => async (dispatch, getState) => {
  const response = await api.get(`/search/${searchterm}`);
  dispatch({ type: ADD_BOOKMARK, payload: response.data });
};

export const deleteBookmark = () => async (dispatch, getState) => {};

export const fetchBookmarks = () => async (dispatch, getState) => {};
