import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { searchRepo } from "../../store/actions";
import SearchBar from "../SearchBar/SearchBar";
import SearchResults from "../SearchResults/SearchResults";

const SearchPage = (props) => {
  const [searchList, setSearchList] = useState();

  const fetchData = (searchTerm) => {
    props.searchRepo(searchTerm);
  };

  useEffect(() => {
    setSearchList(props.search.repos);
    return () => {
      delete props.search.repos;
    };
  }, []);

  if (props.search.loading) {
    return <div>Loading ...</div>;
  }

  return (
    <>
      <SearchBar onFetchRepos={fetchData} />
      <SearchResults searchList={props.search.repos} />
    </>
  );
};

const mapStateToProps = (state) => {
  return {
    search: state.search,
  };
};

export default connect(mapStateToProps, { searchRepo })(SearchPage);
