import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { login, signup } from "../../store/actions";

import "./Auth.css";

const Auth = (props) => {
  const [isLoginMode, setIsLoginMode] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailIsValid, setEmailIsValid] = useState(true);
  const [passwordIsValid, setPasswordIsValid] = useState(true);
  const [emailIsTouched, setEmailIsTouched] = useState(false);
  const [passwordIsTouched, setPasswordIsTouched] = useState(false);
  const [formIsValid, setFormIsValid] = useState(true);

  useEffect(() => {
    if (props.auth.user.token) {
      props.history.push("/");
    }
  }, []);

  const switchModeHandler = () => {
    setIsLoginMode((prevMode) => !prevMode);
  };

  const handleChangeEmail = (event) => {
    setEmail(event.target.value);
    if (event.target.value.trim() !== "") {
      setEmailIsValid(true);
      return;
    }
  };

  const handleChangePassword = (event) => {
    setPassword(event.target.value);
    if (event.target.value.trim() !== "") {
      setPasswordIsValid(true);
      return;
    }
  };

  const authSubmitHandler = (event) => {
    event.preventDefault();

    setEmailIsTouched(true);
    setPasswordIsTouched(true);

    if (email.trim() === "" || password.trim() === "") {
      setFormIsValid(false);
      return;
    }

    if (email.trim() === "") {
      setEmailIsValid(false);
      return;
    }

    if (password.trim() === "") {
      setPasswordIsValid(false);
      return;
    }

    setEmailIsValid(true);
    setPasswordIsValid(true);
    setFormIsValid(true);

    if (isLoginMode) {
      const formData = {
        email,
        password,
      };
      props.login(formData);
    } else {
      const formData = {
        email,
        password,
      };
      props.signup(formData);
    }
  };

  return (
    <React.Fragment>
      <div className="authentication">
        {isLoginMode ? <h2>Login</h2> : <h2>Signup</h2>}
        <hr />
        <div className="error-msg">{props.auth.error}</div>
        <form onSubmit={authSubmitHandler}>
          <input
            name="email"
            placeholder="Email Address"
            onChange={handleChangeEmail}
            value={email}
            type="email"
          />
          {(!emailIsValid && emailIsTouched) ||
            (!formIsValid && (
              <div className="error-msg">Please enter an email</div>
            ))}
          <input
            name="password"
            placeholder="Password"
            onChange={handleChangePassword}
            value={password}
            type="password"
          />
          {(!passwordIsValid && passwordIsTouched) ||
            (!formIsValid && (
              <div className="error-msg">Please enter a valid password</div>
            ))}
          <button type="submit" className="btn submit-btn">
            {isLoginMode ? "LOGIN" : "SIGNUP"}
          </button>
        </form>
        <button onClick={switchModeHandler} className="btn switch-btn">
          SWITCH TO {isLoginMode ? "SIGNUP" : "LOGIN"}
        </button>
      </div>
    </React.Fragment>
  );
};

const mapStateToProps = (state) => {
  return {
    auth: state.auth,
  };
};

export default connect(mapStateToProps, { login, signup })(Auth);
