import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { searchRepo } from "../store/actions";
import SearchBar from "./SearchBar";
import SearchResults from "./SearchResults";

const SearchPage = (props) => {
  const [searchList, setSearchList] = useState();

  const fetchData = (searchTerm) => {
    props.searchRepo(searchTerm);
  };

  useEffect(() => {
    fetchData();
    setSearchList(props.search.repos);
  }, [props.search.repos]);

  return (
    <>
      <h1>Country List</h1>
      <SearchBar onFetchRepos={fetchData} />
      <SearchResults searchList={searchList} />
    </>
  );
};

const mapStateToProps = (state) => {
  return {
    search: state.search,
  };
};

export default connect(mapStateToProps, { searchRepo })(SearchPage);
