import { LOGIN, SIGNUP } from "../actions/types";

const INITAL_STATE = {};
export default (state = INITAL_STATE, action) => {
  switch (action.type) {
    case LOGIN:
      return { ...state, user: action.payload };
    case SIGNUP:
      return { ...state, user: action.payload };
    default:
      return state;
  }
};
